        if(x === 'myCourse'){
            setMyCourse(true)
            setInProgress(false)
            setNotification(false)
            setCompleted(false)
            setCertificate(false)
        }
        if(x === 'inprogress'){
            setMyCourse(false)
            setInProgress(true)
            setNotification(false)
            setCompleted(false)
            setCertificate(false)
        }
        if(x === 'Notifications'){
            setMyCourse(false)
            setInProgress(false)
            setNotification(true)
            setCompleted(false)
            setCertificate(false)
        }
        if(x === 'Completed'){
            setMyCourse(false)
            setInProgress(false)
            setNotification(false)
            setCompleted(true)
            setCertificate(false)
        }
        if(x === 'Certificate'){
            setMyCourse(false)
            setInProgress(false)
            setNotification(false)
            setCompleted(false)
            setCertificate(true)
        }
    }

