import React, { useState, useRef, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Footer from '../Footer/Footer';
import NavBar from '../Navbar/NavBar';
import SignInButton from './SignInbutton';

import './SignUp.css';

export default function SignUp() {
    const [formData, setFormData] = useState(() => {
        const storedData = localStorage.getItem('RegisterUser');
        return storedData ? JSON.parse(storedData) : {
            firstName: '',
            surname: '',
            DOB: '',
            gender: '',
            whatsappNum: '',
            email: '',
            password: '',
            confirmPassword: '',
        };
    });

    const [focusedField, setFocusedField] = useState('');

    const inputRefs = useRef({
        firstName: useRef(null),
        surname: useRef(null),
        DOB: useRef(null),
        gender: useRef(null),
        whatsappNum: useRef(null),
        email: useRef(null),
        password: useRef(null),
        confirmPassword: useRef(null),
    });

    const handleInputFocus = (fieldName) => {
        setFocusedField(fieldName);
    };

    const handleInputBlur = (fieldName) => {
        if (!inputRefs.current[fieldName].current.value && focusedField === fieldName) {
            setFocusedField('');
        }
    };

    useEffect(() => {
        localStorage.setItem('RegisterUser', JSON.stringify(formData));
    }, [formData]);

    const handleChange = (event) => {
        const { name, value } = event.target;
        setFormData((prevState) => ({
            ...prevState,
            [name]: value,
        }));
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        // Add your form submission logic here
    };

    return (
        <>
            <NavBar />
            <div className="signup">
                <div className="signupHeader">
                    <Link to="/">
                        <div className="backarrow">
                            <img src="https://res.cloudinary.com/do5wu6ikf/image/upload/v1684452441/starpenzu/back_qpybto.svg" alt="" />
                        </div>
                    </Link>
                    <div className="signupTitle">Register</div>
                </div>
                <form onSubmit={handleSubmit} className="register">
                    <div className={`outlined-input-container ${focusedField === 'firstName' ? 'focused' : ''}`}>
                        <input
                            type="text"
                            className="outlined-input"
                            name="firstName"
                            value={formData.firstName}
                            ref={inputRefs.current.firstName}
                            onFocus={() => handleInputFocus('firstName')}
                            onBlur={() => handleInputBlur('firstName')}
                            onChange={handleChange}
                        />
                        <label className={`outlined-label ${focusedField === 'firstName' || formData.firstName ? 'active' : ''}`}>First Name</label>
                    </div>
                    {/* Repeat the same pattern for other input fields */}
                    {/* Surname */}
                    <div className={`outlined-input-container ${focusedField === 'surname' ? 'focused' : ''}`}>
                        <input
                            type="text"
                            className="outlined-input"
                            name="surname"
                            value={formData.surname}
                            ref={inputRefs.current.surname}
                            onFocus={() => handleInputFocus('surname')}
                            onBlur={() => handleInputBlur('surname')}
                            onChange={handleChange}
                        />
                        <label className={`outlined-label ${focusedField === 'surname' || formData.surname ? 'active' : ''}`}>Surname</label>
                    </div>
                    {/* Date of Birth */}
                    <div className={`outlined-input-container ${focusedField === 'DOB' ? 'focused' : ''}`}>
                        <input
                            type="date"
                            className="outlined-input"
                            name="DOB"
                            value={formData.DOB}
                            ref={inputRefs.current.DOB}
                            onFocus={() => handleInputFocus('DOB')}
                            onBlur={() => handleInputBlur('DOB')}
                            onChange={handleChange}
                        />
                        <label className={`outlined-label ${focusedField === 'DOB' || formData.DOB ? 'active' : ''}`}>Date of Birth</label>
                    </div>
                    {/* Gender */}
                    <div className={`outlined-input-container ${focusedField === 'gender' ? 'focused' : ''}`}>
                        <input
                            type="text"
                            className="outlined-input"
                            name="gender"
                            value={formData.gender}
                            ref={inputRefs.current.gender}
                            onFocus={() => handleInputFocus('gender')}
                            onBlur={() => handleInputBlur('gender')}
                            onChange={handleChange}
                        />
                        <label className={`outlined-label ${focusedField === 'gender' || formData.gender ? 'active' : ''}`}>Gender</label>
                    </div>
                    {/* WhatsApp Number */}
                    <div className={`outlined-input-container ${focusedField === 'whatsappNum' ? 'focused' : ''}`}>
                        <input
                            type="text"
                            className="outlined-input"
                            name="whatsappNum"
                            value={formData.whatsappNum}
                            ref={inputRefs.current.whatsappNum}
                            onFocus={() => handleInputFocus('whatsappNum')}
                            onBlur={() => handleInputBlur('whatsappNum')}
                            onChange={handleChange}
                        />
                        <label className={`outlined-label ${focusedField === 'whatsappNum' || formData.whatsappNum ? 'active' : ''}`}>WhatsApp Number</label>
                    </div>
                    {/* Email */}
                    <div className={`outlined-input-container ${focusedField === 'email' ? 'focused' : ''}`}>
                        <input
                            type="email"
                            className="outlined-input"
                            name="email"
                            value={formData.email}
                            ref={inputRefs.current.email}
                            onFocus={() => handleInputFocus('email')}
                            onBlur={() => handleInputBlur('email')}
                            onChange={handleChange}
                        />
                        <label className={`outlined-label ${focusedField === 'email' || formData.email ? 'active' : ''}`}>Email</label>
                    </div>
                    {/* Password */}
                    <div className={`outlined-input-container ${focusedField === 'password' ? 'focused' : ''}`}>
                        <input
                            type="password"
                            className="outlined-input"
                            name="password"
                            value={formData.password}
                            ref={inputRefs.current.password}
                            onFocus={() => handleInputFocus('password')}
                            onBlur={() => handleInputBlur('password')}
                            onChange={handleChange}
                        />
                        <label className={`outlined-label ${focusedField === 'password' || formData.password ? 'active' : ''}`}>Password</label>
                    </div>
                    {/* Confirm Password */}
                    <div className={`outlined-input-container ${focusedField === 'confirmPassword' ? 'focused' : ''}`}>
                        <input
                            type="password"
                            className="outlined-input"
                            name="confirmPassword"
                            value={formData.confirmPassword}
                            ref={inputRefs.current.confirmPassword}
                            onFocus={() => handleInputFocus('confirmPassword')}
                            onBlur={() => handleInputBlur('confirmPassword')}
                            onChange={handleChange}
                        />
                        <label className={`outlined-label ${focusedField === 'confirmPassword' || formData.confirmPassword ? 'active' : ''}`}>Confirm password</label>
                    </div>
                    <SignInButton className="registerBtn" buttonName="Register" />
                    <div className="already">
                        Already have an account?{' '}
                        <Link className="link-d" to="/Login">
                            <span className="loginn">Sign In</span>
                        </Link>
                    </div>
                </form>
            </div>
            <Footer />
        </>
    );
}
