import React from 'react';
import Footer from '../Footer/Footer';
import NavBar from '../Navbar/NavBar';
import { TextField , MenuItem  } from '@mui/material';
import { makeStyles, multiTextField } from '@mui/styles';
import './SignUp.css'
import { createTheme, ThemeProvider } from '@mui/material/styles';
import Button from '@mui/material/Button';
// import { createTheme, ThemeProvider } from '@mui/material/styles';
// import { purple } from '@mui/material/colors';

// const Mytheme = createTheme(
//     {
//         shape:{
//             borderRadius: 10
//         }
//     }
// )


const useStyles = makeStyles({
    field: {
        marginBlock: '20px',
        width: '250px',
        borderRadius: '10px',
        fontSize: 14
    }
})

const SelectGender = [
    {
        value: 'Male',
        label: 'Male',
    },
    {
        value: 'Female',
        label: 'Female',
    },
    {
        value: 'Others',
        label: 'Others',
    }
    ]

function SignUp() {

    const classes = useStyles();

    const [formData, setFormData] = React.useState(
        {
            firstName: "",
            surname: "",
            DOB: '',
            gender:''
            // whatsappNum: '',
            // email: '',
            // password: '',
            // confirm-password: '',
        }
    );

    function handleChange(event) {

        const {name, type, value} = event.target;
        // setting your new state
        setFormData(prevState => (
            {
                ...prevState,
                [name]: type === value
            }
        ))
        // console.log(event.target)
    }



   return(
       <>
           <NavBar/>
           <div className="signup">

               <div className="signupHeader">
                   <div className="backarrow">
                       <img src="https://res.cloudinary.com/do5wu6ikf/image/upload/v1684452441/starpenzu/back_qpybto.svg" alt=""/>
                   </div>

                   <div className="signupTitle">
                       Register
                   </div>
               </div>
                {/*<ThemeProvider theme={Mytheme}>*/}
               <form action="" noValidate={true} className="register">
                   <TextField
                       className={classes.field}
                       id="outlined-basic"
                       label="First Name"
                       color='secondary'
                       variant="outlined"
                       margin="dense"
                       type='text'
                       gutterBottom ={true}
                       required
                       name={"firstName"}
                       value={formData.firstName}
                       onChange={handleChange}
                   />
                   {/*<DatePicker sx={{ color: 'red' }/>*/}
                   <TextField
                       className={classes.field}
                       id="outlined-basic"
                       label="Surname"
                       color='secondary'
                       variant="outlined"
                       margin="dense"
                       type='text'
                       required
                       name={"surname"}
                       value={formData.surname}
                       onChange={handleChange}
                   />
                     <TextField
                       className={classes.field}
                       id="outlined-basic"
                       label="Date of Birth"
                       color='secondary'
                       InputLabelProps={{
                           shrink: true,
                       }}
                       margin="dense"
                       variant="outlined"
                       type='date'
                       name={"DOB"}
                       required
                       value={formData.DOB}
                       onChange={handleChange}
                   />

                     <TextField
                       className={classes.field}
                       id="outlined-basic"
                       label="Gender"
                       color='secondary'
                       margin="dense"
                       variant="outlined"
                       select
                       type='select'
                       name={"gender"}
                       required
                       // value={formData.gender}
                       onChange={handleChange}
                       defaultValue="EUR"
                   > {SelectGender.map((option) => (
                         <MenuItem key={option.value} value={option.value}>
                             {option.label}
                         </MenuItem>
                     ))}
                     </TextField>

               </form>
                {/*</ThemeProvider>*/}



           </div>

           <Footer/>
       </>

   )
}

export default SignUp;
