import React from 'react';


export default function CoursesPageContent ({courses, index }){

    const [accordion, setAccordion] = React.useState(null);

    function handleAccordion(index) {
        if (accordion === index) {
            setAccordion(null);
        } else {
            setAccordion(index);
        }
    }

    return (
        <>
            {courses.map((course, index) => (
                <div className="CoursesPageContent" key={index}>
                    <div className="CoursesPageContenttext" >
                        {course.title}
                    </div>
                    <img src={accordion === index ? course.img2 : course.img} alt="" onClick={() => handleAccordion(index)} />

                </div>
            ))}

            {accordion === index && (
                <div className="accordionContents">
                    {course.stages.map((stage, stageIndex) => (
                        <div className="accordionContentsLists" key={stageIndex}>
                            <div>{stage}</div>
                            {course.locked && <img src={course.imgLock} alt="" />}
                        </div>
                    ))}
                </div>
            )}
        </>
    );
}
