import React from 'react';


export default function CoursesPageContent (props){

    const [accordion ,setAccordion] = React.useState(false);

    function handleAccordion(){
        if(!accordion){
            setAccordion(true)
        }else{
            setAccordion(false)
        }


    }

    return(
        <>
            <div className='CoursesPageContent' >
            <div className="CoursesPageContenttext">{props.coursesStages}</div>

             <img src={accordion ? props.img2 : props.img} alt="" onClick={handleAccordion} />
        </div>

            {accordion &&
                <div className="accordionContents">
                    <div className="accordionContentsLists">
                        <div>{props.CoursesContent1}</div>
                        <div>{props.CoursesContent2}</div>
                        <div>{props.CoursesContent3}</div>
                        <img src={props.imgLock} alt=""/>
                    </div>

                </div>}
        </>

    )
}
